    //    })
    // });
  }, []);

  const Login = input => {

    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email: input.email, password: input.password })
    };
    const urlFetch = fetch('http://127.0.0.1:5000/login', requestOptions);
    urlFetch.then( res => {
      console.log('mlebu 1', res);
       if(res.status === 200){
         return res.json()   
       } else {
        return res.json() 
       }
    }).then( resJson => {
      console.log('mlebu 2', resJson);
      // if()
       setData({
           data: resJson
       })
       setCurrentUser(resJson.data)
    });
  }

  const Logout = () => {
    console.log("Logout");
  }

  return (
    <div className="App">
      {(currentUser != "") ? (
        <div>
          <h2>Selamat datang <span>{currentUser.data.nama}</span></h2>
        </div>
      ) : (
        <LoginForm Login={Login} error={error}/>
      )}
    </div>
  );
}

export default App;
